import styled from "styled-components";
import React, {useState} from "react";
interface padProps {
    children?:React.ReactNode;
    noteValue:string;
    noteValueOnChange: (value: string)=>void;
    padValue:number;
    padValueOnChange:(padValue:number) => void;
    onOK?:()=>void;
}
const NumberPad = styled.section`
  display: flex;
  flex-direction: column;
  background-color: rgb(246,209,105);
  border-top: 2px solid black;
  border-bottom-right-radius: 10px;
  border-bottom-left-radius: 10px;
  font-weight: bolder;
  margin-right: 0.5px;
    >.note{
      margin-top: 5px;
      display: flex;
      height: 36px;
      text-align: center;
      flex-direction: row;
      align-items: center;
      padding-right: 15px;
      padding-left: 15px;
      flex-wrap: nowrap;
      font-size: 20px;
      >input{
        height: 100%;
        margin-right: 5px;
        margin-left: 5px;
        background: none;
        border:none;
        max-width: 30%;
      }
      .output{
        width: auto;
        line-height: 72px;
      }
    }
  >.clearfix::after{
    content: "";
    display: block;
    clear:both;
  }
  >.pad{
    padding-left: 1%;
    > button{
      background-color: rgb(255,255,255);
      box-shadow: inset 0 -5px 0px rgba(0,0,0,0.25);
      border-radius: 10px;
      border: 2px solid black;
      margin-right: 1%;
      margin-bottom: 1%;
      font-size: 18px;
      float:left;
      width: 24%;
      height: 56px;
      font-weight: bolder;  
      &.OK{
        height: 116px;
        float: right;
        background-color: rgb(232,130,148);
        color:white;
      }
      &.dot{
        width: 49%;
      }
    }
  }
`
const NumberPadSection: React.FC<padProps> = (props) => {
    // const [note,setNote] = useState('');
    // console.log(note)
    const note = props.noteValue;
    // const [output,_setOutput] = useState('0');
    // const output = props.padValue;
    const output = props.padValue.toString();
    let number;
    const setOutput = (output:string) => {
        if(output.length > 12){
            number = parseFloat(output.slice(0,12))
        }else if (output.length === 0){
            number = 0;
        }else{
            number = parseFloat(output);
        }
       props.padValueOnChange(number);
    };
    const onClickNumberPad = (e:React.MouseEvent) => {
        let text = (e.target as HTMLButtonElement).textContent;
        console.log(text);
        switch (text){
            case'0':
            case'1':
            case'2':
            case'3':
            case'4':
            case'5':
            case'6':
            case'7':
            case'8':
            case'9':
                if(output === '0'){
                    setOutput(text);
                }else{
                    setOutput(output+ text)
                }
                break;
            case'.':
                if(output.indexOf('.')>=0){return}
                setOutput(output+'.');
                break;
            case'DELETE':
                if(output.length === 1) {
                    setOutput('0')
                }else {
                    setOutput(output.slice(0,output.length-1))
                }
                break;
            case'CLEAR':
                setOutput('0')
                break;
            case'OK':
                if (props.onOK)
                console.log('OK')
                break;
        }
    }
    return (
        <NumberPad>
            <div className="note">
                备注:
                <input placeholder="添加备注" type="text"
                       value = {note}
                       onChange={(e) => props.noteValueOnChange(e.target.value)}
                />
                $:
                <div className="output">
                    {output}
                </div>
            </div>
                <div className="pad clearfix" onClick={onClickNumberPad}>
                    <button>1</button>
                    <button>2</button>
                    <button>3</button>
                    <button>DELETE</button>
                    <button>4</button>
                    <button>5</button>
                    <button>6</button>
                    <button>CLEAR</button>
                    <button>7</button>
                    <button>8</button>
                    <button>9</button>
                    <button className="OK">OK</button>
                    <button>0</button>
                    <button className="dot">.</button>
                </div>
        </NumberPad>
);
}
export {NumberPadSection};
