import styled from "styled-components";
import React, {useState} from "react";
import 'index.scss';
interface padProps {
    children?:React.ReactNode;
}
const NumberPad = styled.section`
  display: flex;
  flex-direction: column;
  background-color: rgb(246,209,105);
  border-top: 2px solid black;
  border-bottom-right-radius: 10px;
  border-bottom-left-radius: 10px;
  font-weight: bolder;
  margin-right: 0.5px;
    >.note{
      margin-top: 5px;
      display: flex;
      height: 36px;
      text-align: center;
      flex-direction: row;
      align-items: center;
      padding-right: 15px;
      padding-left: 15px;
      flex-wrap: nowrap;
      font-size: 20px;
      >input{
        height: 100%;
        margin-right: 5px;
        margin-left: 5px;
        background: none;
        border:none;
        max-width: 30%;
      }
      .output{
        width: auto;
        line-height: 72px;
      }
    }
  >.clearfix::after{
    content: "";
    display: block;
    clear:both;
  }
  >.pad{
    padding-left: 1%;
    > button{
      background-color: rgb(255,255,255);
      box-shadow: inset 0 -5px 0px rgba(0,0,0,0.25);
      border-radius: 10px;
      border: 2px solid black;
      margin-right: 1%;
      margin-bottom: 1%;
      font-size: 18px;
      float:left;
      width: 24%;
      height: 56px;
      font-weight: bolder;  
      &.OK{
        height: 116px;
        float: right;
        background-color: rgb(232,130,148);
        color:white;
      }
      &.dot{
        width: 49%;
      }
      &.buttonDown {
        border: 3px solid rgb(246,100,100);
        background-color: rgb(246,209,181);
      }
    }
  }
`
const NumberPadSection: React.FC<padProps> = ({children}) => {
    const [note,setNote] = useState('');
    console.log(note)
    const [output,setOutput] = useState('0');
    const onClickNumberPad = (e) => {
        console.log(e.target);
    }
    return (
        <NumberPad>
            <div className="note">
                备注:
                <input placeholder="添加备注" type="text"
                       value = {note}
                       onChange={(e) => setNote(e.target.value)}
                />
                $:
                <div className="output">
                    12345678910
                </div>
            </div>
                <div className="pad clearfix" onClick={onClickNumberPad}>
                    <button onClick={()=>{onClickNumberPad(1)}}>1</button>
                    <button onClick={()=>{onClickNumberPad(2)}}>2</button>
                    <button onClick={()=>{onClickNumberPad(3)}}>3</button>
                    <button>DELETE</button>
                    <button onClick={()=>{onClickNumberPad(4)}}>4</button>
                    <button onClick={()=>{onClickNumberPad(5)}}>5</button>
                    <button onClick={()=>{onClickNumberPad(6)}}>6</button>
                    <button>CLEAR</button>
                    <button onClick={()=>{onClickNumberPad(7)}}>7</button>
                    <button onClick={()=>{onClickNumberPad(8)}}>8</button>
                    <button onClick={()=>{onClickNumberPad(9)}}>9</button>
                    <button className="OK">OK</button>
                    <button onClick={()=>{onClickNumberPad(0)}}>0</button>
                    <button className="dot">.</button>
                </div>
        </NumberPad>
);
}
export {NumberPadSection};
