import Icon from "./Icon";
import {Link} from "react-router-dom";
import React from "react";
import styled from "styled-components";
const NaviWrapper = styled.div`
  border: 1px solid grey;
  >ul {
    display: flex;
    flex-direction: row;
    justify-content: space-evenly;

    > a {
      > li {
        width: 80px;
        height: 80px;
        text-align: center;
        border-radius: 50%;
        border: 1px solid green;
        display: flex;
        flex-direction: column;
        align-items: center;

        .icon {
          width: 25px;
          height: 25px;
          display: inline-block;
          margin-top: 18px;
        }
      }
    }
  }
`

const Navi = () =>{
    return(
        <NaviWrapper>
            <ul>
                    <Link to="/Home">
                        <li><Icon name="home"></Icon>主页</li>
                    </Link>
                <li>
                    <Link to="/Count"><Icon name="plus-cross"></Icon>记账</Link>
                </li>
                <li>
                    <Link to="/Statistics"><Icon name="analysis"></Icon>统计</Link>
                </li>
            </ul>
        </NaviWrapper>
    );
}

export  default  Navi;
