import Layout from "../components/Layout";
import React from "react";
import styled from "styled-components";
import {useRecords} from "../hooks/useRecords";
import {useTags} from "../hooks/useTags";
import Icon from "../components/Icon";
import dayjs from "dayjs"
const StatisticsLayOut = styled(Layout)`
>header{
  background-color:rgb(246,208,164);
  height: 50px;
  width: 100%;
  display: flex;
  align-items: center;
  justify-content: center;
  font-weight: bolder;
  font-size: 20px;
}
  >ul{  
    display: flex;
    flex-direction: column;
    padding-left: 8px;
    padding-right: 8px;
    overflow-y: scroll;
    flex-grow: 1;
    font-weight: 10000000000000;
    >li{
      flex-shrink: 0;
      box-shadow: inset -2px -3px 0px rgba(0,0,0,0.25);
      background-color:rgb(254,251,240) ;
      border-radius: 5px;
      display: flex;
      align-items: center;
      height: 40px;
      width: 100%;
      margin-bottom: 4px;
      z-index: 2;
      border: 1px solid black;
      >svg{
        height: 30px;
        width: 30px;
      }
    }
  }
`
function Statistics() {
    const {records} = useRecords()
    const {iconMap,getName} = useTags()
    return (
        <StatisticsLayOut>
            <header>
                收支明细
            </header>
            <ul>
                {records.map(record => {
                    return <li>
                        <Icon name={iconMap[record.tagIds[0]]}/>
                        {getName(record.tagIds[0])}
                        {record.category==='-'?'支出':'收入'}
                        {record.amount}
                        {/*{dayjs(record.createdAt).format('YYYY年MM月DD日')}*/}
                        周{dayjs(record.createdAt).day()}
                    </li>
                })}
            </ul>
        </StatisticsLayOut>
    );
}
export default Statistics;
