import styled from "styled-components";
import React, {useState} from "react";
import Icon from "../../components/Icon";
interface TagProps {
    children?:React.ReactNode;
}
const TagList = styled.ol`
  display: flex;
  flex-direction: row;
  flex-wrap: wrap;
  //border: 1px solid black;
  width: 370px;
  padding : 20px;
  flex-grow: 1;
  background-color: rgb(254,251,240);
  align-content:flex-start;
  >li{
    background-color: rgb(246,208,160);
    width: 50px;
    height: 50px;
    margin-right: 20px;
    margin-bottom: 20px;
    text-align: center;
    border: 2px solid black;
    border-radius: 50%;
    display: flex;
    flex-direction: column;
    align-items: center;
    font: inherit;
    font-size: 12px;
    font-weight: bolder;
    &.last{
      
    }
    .icon{
      display: inline-block;
      margin-top: 2px;
      fill:none;
    }
  }
  li:nth-child(5n){
    margin-right: 0px;
  }
`
const TagsSection: React.FC<TagProps> = ({children}) => {
    const [tags, setTags] = useState(['餐饮','房租','服装','娱乐','旅行','美容','汽车','饮品','宠物','购物','度假']);
    const [tagsIcon,setTagsIcon] = useState(['food','rent','clothes','entertainment','travel','makeup','car','beverage','pet','shopping','vacation']);
    return(
        <TagList>
            {
                    tags.map(tag =>
                        <li key={tag}>{
                            tagsIcon.map(tagsIcon =>
                                <Icon key={tagsIcon} name={tagsIcon}></Icon>
                            )
                        }
                    {tag}</li>
                )
            }
            {/*<li><Icon name="food" ></Icon>餐饮</li>*/}
            {/*<li><Icon name="rent" ></Icon>房租</li>*/}
            {/*<li><Icon name="clothes" ></Icon>服装</li>*/}
            {/*<li><Icon name="entertainment" ></Icon>娱乐</li>*/}
            {/*<li><Icon name="travel" ></Icon>旅行</li>*/}
            {/*<li><Icon name="makeup" ></Icon>美容</li>*/}
            {/*<li><Icon name="car" ></Icon>汽车</li>*/}
            {/*<li><Icon name="beverage" ></Icon>饮品</li>*/}
            {/*<li><Icon name="pet" ></Icon>宠物</li>*/}
            {/*<li><Icon name="shopping" ></Icon>购物</li>*/}
            {/*<li><Icon name="vacation" ></Icon>度假</li>*/}
            {/*<li className="last"><Icon name="settings"/>设置</li>*/}
        </TagList>
    );
}

export {TagsSection};
