import styled from "styled-components";
import React, {useState} from "react";
interface padProps {
    children?:React.ReactNode;
}
const NumberPad = styled.section`
  display: flex;
  flex-direction: column;
  background-color: rgb(246,209,105);
  border-top: 2px solid black;
  border-bottom-right-radius: 10px;
  border-bottom-left-radius: 10px;
  font-weight: bolder;
  margin-right: 0.5px;
    >.note{
      margin-top: 5px;
      display: flex;
      height: 36px;
      text-align: center;
      flex-direction: row;
      align-items: center;
      padding-right: 15px;
      padding-left: 15px;
      flex-wrap: nowrap;
      font-size: 20px;
      >input{
        height: 100%;
        margin-right: 5px;
        margin-left: 5px;
        background: none;
        border:none;
        max-width: 30%;
      }
      .output{
        width: auto;
        line-height: 72px;
      }
    }
  >.pad{
    padding-left: 1%;
    > button{
      background-color: rgb(255,255,255);
      box-shadow: inset 0 -5px 0px rgba(0,0,0,0.25);
      border-radius: 10px;
      border: 2px solid black;
      margin-right: 1%;
      margin-bottom: 1%;
      font-size: 18px;
      float:left;
      width: 24%;
      height: 56px;
      font-weight: bolder;  
      &.OK{
        height: 116px;
        float: right;
        background-color: rgb(232,130,148);
        color:white;
      }
      &.dot{
        width: 49%;
      }
      &.buttonDown {
        border: 3px solid rgb(246,100,100);
        background-color: rgb(246,209,181);
      }
    }
  }
`
const NumberPadSection: React.FC<padProps> = ({children}) => {
    const [note,setNote] = useState('');
    console.log(note)
    const [buttonDownOrNot,setButtonDownOrNot] = useState(1)
    const buttonList = document.getElementsByTagName('button')
    for(let i=0;i<buttonList.length;i++){
        let x = buttonList[i]
        x.onclick = () => {
            setButtonDownOrNot(0);
        }
        setButtonDownOrNot(1);
    }
    return (
        <NumberPad>
            <div className="note">
                备注:
                <input placeholder="添加备注" type="text"
                       value = {note}
                       onChange={(e) => setNote(e.target.value)}
                />
                $:
                <div className="output">
                    12345678910
                </div>
            </div>
                <div className="pad">
                    <button className={buttonDownOrNot === 0 ? 'buttonDown':''}>1</button>
                    <button className={buttonDownOrNot === 0 ? 'buttonDown':''}>2</button>
                    <button className={buttonDownOrNot === 0 ? 'buttonDown':''}>3</button>
                    <button className={buttonDownOrNot === 0 ? 'buttonDown':''}>DELETE</button>
                    <button className={buttonDownOrNot === 0 ? 'buttonDown':''}>4</button>
                    <button className={buttonDownOrNot === 0 ? 'buttonDown':''}>5</button>
                    <button className={buttonDownOrNot === 0 ? 'buttonDown':''}>6</button>
                    <button className={buttonDownOrNot === 0 ? 'buttonDown':''}>CLEAR</button>
                    <button className={buttonDownOrNot === 0 ? 'buttonDown':''}>7</button>
                    <button className={buttonDownOrNot === 0 ? 'buttonDown':''}>8</button>
                    <button className={buttonDownOrNot === 0 ? 'buttonDown':''}>9</button>
                    <button className={buttonDownOrNot === 0 ? 'buttonDown':''}>OK</button>
                    <button className={buttonDownOrNot === 0 ? 'buttonDown':''}>0</button>
                    <button className={buttonDownOrNot === 0 ? 'buttonDown':null.join('')}>.</button>
                </div>
        </NumberPad>
);
}
export {NumberPadSection};
