import styled from "styled-components";
import React, {useState} from "react";
import Icon from "../../components/Icon";
interface TagProps {
    children?:React.ReactNode;
    selected:string[];
    onChange: () => void;
}
const TagListSection = styled.ol`
  display: flex;
  flex-direction: row;
  flex-wrap: wrap;
  //border: 1px solid black;
  width: 370px;
  padding : 20px;
  flex-grow: 1;
  background-color: rgb(254,251,240);
  align-content:flex-start;
  
  >li{
    background-color: rgb(246,208,160);
    width: 50px;
    height: 50px;
    margin-right: 20px;
    margin-bottom: 20px;
    text-align: center;
    border: 2px solid black;
    border-radius: 50%;
    display: flex;
    flex-direction: column;
    align-items: center;
    font: inherit;
    font-size: 12px;
    font-weight: bolder;
    &.selected{
      border: 3px solid rgb(246,100,100);
      background-color: rgb(246,209,181);
    }
    &.last{
      
    }
    .icon{
      display: inline-block;
      margin-top: 2px;
      fill:none;
    }
  }
  li:nth-child(5n){
    margin-right: 0px;
  }
`
const TagsSection: React.FC<TagProps> = (props) => {
    const [tags, setTags] = useState<string[]>(['餐饮','房租','服装','娱乐','旅行','美容','汽车','饮品','宠物','购物','度假']);
    //const iconMap =['餐饮','房租','服装','娱乐','旅行','美容','汽车','饮品','宠物','购物','度假'];
    const iconMap:{[k:string]:string} ={'餐饮':'餐饮','房租':'房租','服装':'服装','娱乐':'娱乐','旅行':'旅行','美容':'美容','汽车':'汽车','饮品':'饮品','宠物':'宠物','购物':'购物','度假':'度假',};
    // const [selectedTags,setSelectedTags] = useState<string[]>([]);
    const selectedTags = props.selected;
    const onAddTag = () =>{
        const newTagName = window.prompt('你要新增加的标签名称为')
        console.log(newTagName);
        if (newTagName !== null){
            setTags([...tags,newTagName]);
            console.log(setTags)
        }
    };
    const onToggleTag = (tag:string) => {
        // console.log(typeof setSelectedTags)
        const index = selectedTags.indexOf(tag)
        if (index >= 0){
            props.onChange(selectedTags.filter(t => t !== tag));
        }else {
            setSelectedTags([...selectedTags,tag])
        }
    }
    return(
        <TagListSection>
            {tags.map(tag=>
                <li key={tag} onClick={() => {onToggleTag(tag)}} className={selectedTags.indexOf(tag) >= 0 ? 'selected': ''}>
                    <Icon name={iconMap[tag]||'猫猫'}/>
                    {tag}
                </li>
            )}
            <li id="last" onClick={onAddTag}><Icon name="设置"/>设置</li>
        </TagListSection>
    );
}

export {TagsSection};
