import styled from "styled-components";
import React, {useState} from "react";
interface padProps {
    children?:React.ReactNode;
}
const NumberPad = styled.section`
  display: flex;
  flex-direction: column;
  background-color: rgb(246,209,105);
  border-top: 2px solid black;
  border-bottom-right-radius: 10px;
  border-bottom-left-radius: 10px;
  font-weight: bolder;
  margin-right: 0.5px;
    >.note{
      margin-top: 5px;
      display: flex;
      height: 36px;
      text-align: center;
      flex-direction: row;
      align-items: center;
      padding-right: 15px;
      padding-left: 15px;
      flex-wrap: nowrap;
      font-size: 20px;
      >input{
        height: 100%;
        margin-right: 5px;
        margin-left: 5px;
        background: none;
        border:none;
        max-width: 30%;
      }
      .output{
        width: auto;
        line-height: 72px;
      }
    }
  >.pad{
    padding-left: 1%;
    > button{
      background-color: rgb(255,255,255);
      box-shadow: inset 0 -5px 0px rgba(0,0,0,0.25);
      border-radius: 10px;
      border: 2px solid black;
      margin-right: 1%;
      margin-bottom: 1%;
      font-size: 18px;
      float:left;
      width: 24%;
      height: 56px;
      font-weight: bolder;  
      &.OK{
        height: 116px;
        float: right;
        background-color: rgb(232,130,148);
        color:white;
      }
      &.dot{
        width: 49%;
      }
    }
  }
`
const NumberPadSection: React.FC<padProps> = ({children}) => {
    const [note,setNote] = useState('');
    return (
        <NumberPad>
            <div className="note">
                备注:
                <input placeholder="添加备注" type="text"
                       value = {note}
                       onChange={(e) => setNote(e.target.value)}
                />
                $:
                <div className="output">
                    12345678910
                </div>
            </div>
                <div className="pad">
                    <button>1</button>
                    <button>2</button>
                    <button>3</button>
                    <button>DELETE</button>
                    <button>4</button>
                    <button>5</button>
                    <button>6</button>
                    <button>CLEAR</button>
                    <button>7</button>
                    <button>8</button>
                    <button>9</button>
                    <button className="OK">OK</button>
                    <button>0</button>
                    <button className="dot">.</button>
                </div>
        </NumberPad>
);
}
export {NumberPadSection};
