  display: flex;
  flex-direction: row;
  flex-wrap: wrap;
  //border: 1px solid black;
  width: 370px;
  padding : 20px;
  flex-grow: 1;
  background-color: rgb(254,251,240);
  align-content:flex-start;
  >li{
    background-color: rgb(246,208,160);
    width: 50px;
    height: 50px;
    margin-right: 20px;
    margin-bottom: 20px;
    text-align: center;
    border: 2px solid black;
    border-radius: 50%;
    display: flex;
    flex-direction: column;
    align-items: center;
    font: inherit;
    font-size: 12px;
    font-weight: bolder;
    &.last{
      
    }
    .icon{
      display: inline-block;
      margin-top: 2px;
      fill:none;
    }
  }
  li:nth-child(5n){
    margin-right: 0px;
  }
`
const TagsSection: React.FC<TagProps> = ({children}) => {
    const [tags, setTags] = useState<string[]>(['餐饮','房租','服装','娱乐','旅行','美容','汽车','饮品','宠物','购物','度假']);
    // const [tagsIcons,setTagsIcon] = useState(['餐饮','房租','服装','娱乐','旅行','美容','汽车','饮品','宠物','购物','度假']);
    const onAddTag = () =>{
        
    }
    return(
        <TagListSection>
            {tags.map(tag=>
                <li>
                    <Icon name={tag}></Icon>
                    {tag}
                </li>
            )}
            <li className="last" onClick={onAddTag}><Icon name="设置"/>设置</li>
        </TagListSection>
    );
}

export {TagsSection};
