import styled from "styled-components";
import React, {useState} from "react";
interface categoryProps {
    children?:React.ReactNode;
}
const Category = styled.section`
    > ul {
      display: flex;
      flex-direction: row;
      background-color: rgb(254,251,240) ;
      margin-bottom: 5px;
      margin-top: 20px;
      height: 50px;
      >li{
        width: 50%;
        text-align: center;
        font: inherit;
        font-size: 24px;
        padding: 10px 0;
        border: 2px solid black;
        border-radius: 10px;
        margin-right: 20px;
        margin-left: 20px ;
        background-color: rgb(246,208,110);
        &.selected{
          background-color: rgb(246,209,180);
          border: 2px solid black;
        }
      }
    }
`

const CategorySection:React.FC<categoryProps> = (children) =>{
    const [category, setCategory] = useState('-');
    return (
        <Category>
            <ul>
                <li className = {category}>支出</li>
                <li>收入</li>
            </ul>
        </Category>
    );
}
export {CategorySection};
