import {useTags} from "useTags";
import {useParams} from "react-router-dom";
import React from "react";
import Icon from "../components/Icon";
import styled from "styled-components";
type Params = {
    id:any
}
const EditWrapper = styled.div`
  font-weight: bolder;
  display: flex;
  flex-direction: column;
  background-color:rgb(254,251,240) ;
  height: 100vh;
  >header{
    background-color:rgb(246,208,164);
    height: 50px;
    width: 100%;
    display: flex;
    align-items: center;
    justify-content: space-between;
    box-shadow: inset -2px -2px 5px rgba(0,0,0,0.25);
    >svg{
      height: 50px;
      width: 50px;
    }
    >span{
      margin-right: 50vw;
      transform: translateX(50%);
    }
  }
  >label{
    margin-top: 5px;
    display: flex;
    height: 100px;
    text-align: center;
    flex-direction: row;
    align-items: center;
    padding-right: 15px;
    padding-left: 15px;
    flex-wrap: nowrap;
    ont-size: 20px;
    background-color: rgb(246,208,164);
    border:3px solid black;
    margin-right: 50px;
    margin-left: 50px;
    margin-top: 30px;
    border-radius: 10px;
    box-shadow: inset -2px -2px 0px rgba(0,0,0,0.25);
    >input{
      font-weight: bolder;
      font-size: inherit;
      overflow-x: hidden;
      height: 100%;
      margin-right: 5px;
      margin-left: 5px;
      background: none;
      border:none;
      max-width: 50%;
    }
  }
    >button{
      position: fixed;
      top: 40vh;
      left:50%;
      background-color: rgba(232,130,148,0.7);
      border:none;
      font-size: 18px;
      padding: 8px;
      transform: translateX(-50%);
      border-radius: 14px;
      z-index: 3;
      color: white;
      font-weight: bolder;
      width: 150px;
      height: 70px;
      font-size: 20px;
      box-shadow: inset -2px -3px 0px rgba(0,0,0,0.25);
    }
  
`
const TagEdit: React.FC = () => {
    const {findTag} = useTags();
    let {id} = useParams<Params>();
    const tag = findTag(parseInt(id));
    console.log(tag.name)
    return(
        <EditWrapper>
            <header>
                <Icon name='return'></Icon>
                <span>编辑标签</span>
            </header>
            <label>
                <span>修改标签名</span>
                <input type= "text" placeholder="输入标签名"/>
            </label>
            <button>
                删除标签
            </button>
        </EditWrapper>
    )
}
export {TagEdit};
