import React from "react";
import styled from "styled-components";
import {Link} from "react-router-dom";
import Icon from "../components/Icon";
import {useTags} from "../useTags";
import {createId} from "../lib/createId";
const TagsBar = styled.div`
  display: flex;
  height: 40px;
  padding-left: 10px;
  align-items: center;
  background-color: rgb(246,209,105);
  margin-top: 5px;
  margin-bottom: 5px;
`
const AddTagBar = styled.button`
  position: fixed;
  top: 93vh;
  left:50%;
  background-color: rgba(232,130,148,0.7);
  border:none;
  font-size: 18px;
  padding: 8px;
  transform: translateX(-50%);
  border-radius: 14px;
  z-index: 3;
  color: white;
  font-weight: bolder;
  box-shadow: inset -2px -3px 0px rgba(0,0,0,0.25);
  @media(min-width: 600px){
    top:600px;
    left:180px
  }
`
const Container = styled.div`
  display: flex;
  height: 100vh;
  background-color: rgb(246,208,162);
  font-family: -apple-system, "Noto Sans", "Helvetica Neue", Helvetica, "Nimbus Sans L", Arial, "Liberation Sans", "PingFang SC", "Hiragino Sans GB", "Noto Sans CJK SC", "Source Han Sans SC", "Source Han Sans CN", "Microsoft YaHei", "Wenquanyi Micro Hei", "WenQuanYi Zen Hei", "ST Heiti", SimHei, "WenQuanYi Zen Hei Sharp", sans-serif;
  font-weight: bolder;
  flex-direction: column;
  @media(min-width: 600px){
    height: 667px;
    width: 375px;
  }
  >a{
    background-color: rgb(232,130,148);
    box-shadow: inset -2px -2px 0px rgba(0,0,0,0.25);
    width: 80px;
    height: 50px;
    text-align: center;
    margin-left: 5px;
    margin-top: 5px;
    border-radius: 10%;
    display: flex;  
    flex-direction: column;
    align-items: center;
    font: inherit;
    font-size: 12px;
    font-weight: bolder;
    >svg{
      margin-bottom: -5px;
    }
  }
`
const TagsList = styled.div`
  display: flex;
  flex-direction: column;
  padding-left: 8px;
  padding-right: 8px;
  overflow-y: scroll;
  flex-grow: 1;
  >li{
    flex-shrink: 0;
    background-color:rgb(254,251,240) ;
    border-radius: 5px;
    display: flex;
    align-items: center;
    height: 40px;
    width: 100%;
    margin-bottom: 4px;
    z-index: 2;
    
    >a{
      flex-shrink: 0;
      box-shadow: inset -2px -3px 0px rgba(0,0,0,0.25);
      background-color:rgb(254,251,240) ;
      border-radius: 5px;
      display: flex;
      align-items: center;
      height: 40px;
      width: 100%;
      flex-direction: row;
      //padding: 10px;
      justify-content: space-between;
      z-index: 2;
      svg{
        height: 30px;
        width: 30px;
      }
      >*{
        margin-left: 10px;
        margin-right: 10px;
      }
    }
  }
`
function TagsSettings(){
    const {tags,setTags,iconMap} = useTags();
    const onADddTag = () => {
        const newTagName = window.prompt('新标签名字为')
        if (newTagName !== null){
            setTags([...tags,{id:createId(),name:newTagName}]);
            console.log(tags)
            console.log(iconMap[1])
        }
    }
    return (
        <Container>
            <Link to='/count'>
                <Icon name='return'/>返回
            </Link>
            <TagsBar>
                标签列表
            </TagsBar>
            <TagsList>
                {tags.map(tag=>
                    <li key={tag.id}>
                        <Link to={'/tagsSettings/'+tag}>
                            <Icon name={iconMap[tag.id]||'猫猫'}/>
                            <span>{tag.name}{' '+'>'}</span>
                        </Link>
                    </li>
                )}
            </TagsList>
            <AddTagBar onClick={onADddTag}>新增标签</AddTagBar>
        </Container>
    )
}
export default  TagsSettings;
