import Layout from "../components/Layout";
import React from "react";
import styled from "styled-components";
import Icon from "../components/Icon";
const TagsSection = styled.section`
  background-color: yellowgreen;
  .note{
    
  }
`
const NotesSection = styled.section`
  
`

const CategorySection = styled.section`
    > ul {
      display: flex;
      flex-direction: row;
      background-color: #c4c4c4;
      >li{
        width: 50%;
        text-align: center;
        font: inherit;
        font-size: 24px;
        padding: 16px 0;
        &.selected{
          border-bottom: 3px solid black;
        }
      }
    }
`
const NumberPadSection = styled.section`
    >.note{
      display: flex;
      height: 72px;
      background-color: yellow;
      text-align: center;
    }
`
const TagList = styled.ol`
  display: flex;
  flex-direction: row;
  flex-wrap: wrap;
  margin: auto;
  //border: 1px solid black;
  width: 370px;
  padding-top: 5px;
  >li{
    background-color: skyblue;
    width: 85px;
    height: 85px;
    margin-right: 10px;
    text-align: center;
    border-radius: 50%;
    display: flex;
    flex-direction: column;
    align-items: center;
    font: inherit;
    font-size: 14px;
    margin-bottom: 5px;
    .icon{
      display: inline-block;
      margin-top: 2px;
      fill:none;
    }
  }
  .last{
    background-color: white;
  }
  li:nth-child(4n){
    margin-right: 0px;
  }
`
function Count() {
    return (
        <Layout>
            <TagsSection>
                <TagList>
                    <li><Icon name="food" ></Icon>餐饮</li>
                    <li><Icon name="rent" ></Icon>房租</li>
                    <li><Icon name="clothes" ></Icon>服装</li>
                    <li><Icon name="bodybuilding" ></Icon>健身</li>
                    <li><Icon name="travel" ></Icon>旅行</li>
                    <li><Icon name="makeup" ></Icon>美容</li>
                    <li><Icon name="car" ></Icon>汽车</li>
                    <li className="last"><Icon name="add"/>添加</li>
                </TagList>
            </TagsSection>
            <CategorySection>
                <ul>
                    <li className = "selected">支出</li>
                    <li>收入</li>
                </ul>
            </CategorySection>
            <NumberPadSection>
                <div className="note">
                    备注
                    <input/>
                    <div className="output"></div>
                </div>
                <div className="pad">
                    <button>1</button>
                    <button>2</button>
                    <button>3</button>
                    <button>DELETE</button>
                    <button>4</button>
                    <button>5</button>
                    <button>6</button>
                    <button>CLEAR</button>
                    <button>7</button>
                    <button>8</button>
                    <button>9</button>
                    <button>OK</button>
                    <button>0</button>
                    <button>.</button>
                </div>
            </NumberPadSection>
        </Layout>
    );
}
export default Count;
